<?php defined('_JEXEC') or die('Restricted access');jimport('joomla.application.component.view');class DevotionsViewDevotions extends JView {    function display ($tpl = null) {        $user =& JFactory::getUser();                $pid = JRequest::getVar('pid', 0, 'get', 'int');        $sword = JRequest::getVar('sword', '', 'get', 'string');        $params =& JComponentHelper::getParams( 'com_devotions' );        $model = $this->getModel('devotions');                $limit = $params->get('devotions_limit');                $devotion_object = $model->getDevotions($pid);        $pagination = $model->getPagination($pid);                $this->assignRef('devotions', $devotion_object);        $this->assignRef('pagination', $pagination);        $this->assignRef('model', $model);        $this->assignRef('pid', $pid);        $this->assignRef('sword', $sword);                parent::display($tpl);    }}