<?php defined('_JEXEC') or die('Restricted access');jimport('joomla.application.component.view');class DevotionsViewDivinecall extends JView {    function display ($tpl = null) {        $model = $this->getModel('divinecall');                $requests = $model->getRequests();                $this->assignRef('requests', $requests);        $this->assignRef('model', $model);                parent::display($tpl);    }}