<?php defined('_JEXEC') or die('Restricted access');jimport('joomla.application.component.view');class DevotionsViewNewdevotion extends JView {    function display ($tpl = null) {        $id = JRequest::getVar('dId', 0, 'get', 'int');        $model =& $this->getModel();        $user = &JFactory::getUser();                $pagination = $model->getPagination($id);        $devotion = $model->getDevotion($id);        $comments = $model->getComments($id);        $pastor = $model->getPastor($devotion->pastor);        $devotions = $model->getRecentDevotions($pastor->id);        $getUrl = $model->getUrls();        $user2 = $model->getUser($user->id);                        $this->assignRef('model', $model);        $this->assignRef('pastor', $pastor);        $this->assignRef('user', $user2);        $this->assignRef('getUrl', $getUrl);        $this->assignRef('devotion', $devotion);        $this->assignRef('devotions', $devotions);        $this->assignRef('comments', $comments);        $this->assignRef('pagination', $pagination);                parent::display($tpl);    }}