<?php defined('_JEXEC') or die('Restricted access');jimport('joomla.application.component.view');class DevotionsViewProfile extends JView {    function display ($tpl = null) {        $pid = JRequest::getVar('pid', 0, 'get', 'int');        $model =& $this->getModel('profile');                $devotion_object = $model->getDevotions($pid);        $partners = $model->getParners($pid);        $num_partners = $model->countParners($pid);        $pastor = $model->getPastor($pid);                $this->assignRef('devotions', $devotion_object);        $this->assignRef('partners', $partners);        $this->assignRef('pastor', $pastor);        $this->assignRef('model', $model);        $this->assignRef('num_partners', $num_partners);                parent::display($tpl);    }}