<?php defined('_JEXEC') or die('Restricted access');jimport('joomla.application.component.view');class DevotionsViewTimeline extends JView {    function display ($tpl = null) {        $params =& JComponentHelper::getParams( 'com_devotions' );        $model = $this->getModel('timeline');        $limit = $params->get('devotions_limit');                $devotion_object = $model->getDevotions();        $pagination = $model->getPagination();                $this->assignRef('devotions', $devotion_object);        $this->assignRef('pagination', $pagination);        $this->assignRef('model', $model);        $this->assignRef('pid', $pid);                parent::display($tpl);    }}